import { CHANGE_INPUT, ADD_ITEM, DELETE_ITEM, GET_LIST, UPDATE_LIST, SAVE_LIST } from './actionTypes'
const defaultState = {
    inputValue: '',
    list: [

    ]
}

const states = (state = defaultState, action) => {

    console.log(state, action)

    //Reducer里只能接收state，不能改变state
    if (action.type === CHANGE_INPUT) {
        let newState = JSON.parse(JSON.stringify(state))
        newState.inputValue = action.value
        return newState
    }else if (action.type === ADD_ITEM) {
        let newState = JSON.parse(JSON.stringify(state))
        newState.list.push(newState.inputValue)
        newState.inputValue = ''
        return newState
    }else if (action.type === DELETE_ITEM) {
        let newState = JSON.parse(JSON.stringify(state))
        newState.list.splice(action.index, 1)
        return newState
    }else if (action.type === GET_LIST) {
        let newState = JSON.parse(JSON.stringify(state))
        newState.list = action.data.data.list
        return newState
    }else if (action.type === UPDATE_LIST) {
        let newState = JSON.parse(JSON.stringify(state))
        newState.list[action.index].isSaved = false
        return newState
    }else if(action.type === SAVE_LIST){
        let newState = JSON.parse(JSON.stringify(state))
        newState.list[action.index].isSaved = true
    }

    return state
}
export default states
